import React from "react";
import "./styles.css";

import DateEditor from "react-tabulator/lib/editors/DateEditor";
import MultiValueFormatter from "react-tabulator/lib/formatters/MultiValueFormatter";
import MultiSelectEditor from "react-tabulator/lib/editors/MultiSelectEditor";

import "react-tabulator/css/bootstrap/tabulator_bootstrap.min.css"; // use Theme(s)

// for React 16.4.x use: import { ReactTabulator } - example in github repo.
import { ReactTabulator, reactFormatter } from "react-tabulator"; // for React 15.x

function SimpleButton(props) {
  const rowData = props.cell._cell.row.data;
  const cellValue = props.cell._cell.value || "Edit | Show";
  return <button onClick={() => alert(rowData.name)}>{cellValue}</button>;
}

const data = [
  {
    id: 1,
    name: "Oli Bob",
    age: "12",
    color: "red",
    dob: "01/01/1980",
    rating: 5,
    passed: true,
    pets: ["cat", "dog"]
  },
  {
    id: 2,
    name: "Mary May",
    age: "1",
    color: "green",
    dob: "12/05/1989",
    rating: 4,
    passed: true,
    pets: ["cat"]
  },
  {
    id: 3,
    name: "Christine Lobowski",
    age: "42",
    color: "green",
    dob: "10/05/1985",
    rating: 4,
    passed: false
  },
  {
    id: 4,
    name: "Brendon Philips",
    age: "125",
    color: "red",
    dob: "01/08/1980",
    rating: 4.5,
    passed: true
  },
  {
    id: 5,
    name: "Margret Marmajuke",
    age: "16",
    color: "yellow",
    dob: "07/01/1999",
    rating: 4,
    passed: false
  },
  {
    id: 6,
    name: "Van Ng",
    age: "37",
    color: "green",
    dob: "06/10/1982",
    rating: 4,
    passed: true,
    pets: ["dog", "fish"]
  },
  {
    id: 7,
    name: "Duc Ng",
    age: "37",
    color: "yellow",
    dob: "10/10/1982",
    rating: 4,
    passed: true,
    pets: ["dog"]
  }
];

// Editable Example:
const colorOptions = {
  [""]: "&nbsp;",
  red: "red",
  green: "green",
  yellow: "yellow"
};
const petOptions = [
  { id: "cat", name: "cat" },
  { id: "dog", name: "dog" },
  { id: "fish", name: "fish" }
];
const editableColumns = [
  {
    title: "Name",
    field: "name",
    width: 150,
    editor: "input",
    headerFilter: "input"
  },
  {
    title: "Age",
    field: "age",
    hozAlign: "left",
    formatter: "progress",
    editor: "progress"
  },
  {
    title: "Favourite Color",
    field: "color",
    editor: "select",
    editorParams: {
      allowEmpty: true,
      showListOnEmpty: true,
      values: colorOptions
    },
    headerFilter: "select",
    headerFilterParams: { values: colorOptions }
  },
  {
    title: "Date Of Birth",
    field: "dob",
    sorter: "date",
    editor: DateEditor,
    editorParams: { format: "MM/DD/YYYY" }
  },
  {
    title: "Pets",
    field: "pets",
    sorter: (a, b) => a.toString().localeCompare(b.toString()),
    editor: MultiSelectEditor,
    editorParams: { values: petOptions },
    formatter: MultiValueFormatter,
    formatterParams: { style: "PILL" }
  },
  {
    title: "Passed?",
    field: "passed",
    hozAlign: "center",
    formatter: "tickCross",
    editor: true
  }
];

export default class HomePage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      nameFilter: ""
    };
  }

  onSelectChange = e => {
    const nameFilter = e.target.value;
    this.setState({ nameFilter });
  };

  render() {
    const filteredData = this.state.nameFilter
      ? data.filter(item => item.name === this.state.nameFilter)
      : data;
    console.log("filteredData", filteredData);
    const columns = [
      { title: "Name", field: "name" },
      { title: "County", field: "county" },
      { title: "DOB", field: "dob" },
      { title: "Charges", field: "charges" },
      { title: "Bail", field: "bail" }
    ];

    const options = {
      movableRows: true,
      responsiveLayout: "collapse",
      pagination: "local",
      paginationSize: 20,
      columnMinWidth: 150
    };

    return (
      <div className="content-container">
        <select onChange={this.onSelectChange}>
          <option value="" />
          <option value="Duc Ng">Duc Ng</option>
          <option value="Van Ng">Van Ng</option>
        </select>
        <ReactTabulator
          columns={columns}
          data={filteredData}
          className="custom-css-class"
          options={options}
        />
      </div>
    );
  }
}
