import React from "react";
import api from '../../config/Api';
import "react-tabulator/lib/css/bootstrap/tabulator_bootstrap.min.css";
import { React15Tabulator } from "react-tabulator";
import axios from 'axios';


const data = [];
class ListaPagos extends React.Component {
  constructor(props) {
    super(props);
    this.consultaVentas = this.consultaVentas.bind(this);
    this.state = { 
        data: '',
        inicio : null,
        final : null
    }
  }

  change(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  //Cargamos los datos de la tabla.
  componentDidMount(){
  }

  //Insertamos los datos.
  consultaVentas(){
    let inicio = this.state.inicio;
    let final = this.state.final;
    axios.post(`${api}/api/informes/ventas/${inicio}/${final}`)
    .then(res => {
        if(res.status === 200){

            let dataVentas = res.data.ventas;
            let arrayDatos = [];
            console.log(dataVentas[0]);
            console.log(dataVentas[0][0]);
            console.log(dataVentas[0].length);
            console.log(Object.keys(dataVentas[0]).length);
            for (let i = 0; i < dataVentas.length; i++) {
                let arrayTemporalVentas = [];
                for (const key in dataVentas[i]) {
                    if (dataVentas[i].hasOwnProperty(key)) {
                        arrayTemporalVentas.push(dataVentas[i][key]);
                    }
                }
                arrayDatos.push(arrayTemporalVentas);
            }
            //console.log(arrayDatos);
            this.setState({ data : res.data.ventas })
        }else{
            
        }
    })
  }

  render() {

    const filteredData = this.state.data
    ? data.filter(item => item.name === this.state.data)
    : data;

    //Agregamos las columnas a la tabla.
    const columns = [
      { title: "Fecha Ingreso", field: "fechaDocumento", width: "9%", headerFilter:"input"},
      { title: "Monto", field: "monto", width: "15%", editor:true, headerFilter:"input"},
      { title: "Tipo documento", field: "IdTipoDocumentoPago", width: "15%", editor:true, headerFilter:"input"}
    ];
    //Opciones de data table
    const options = {
      layout:"id",
      width: "100%",
      pagination:"local",
      paginationSize:30,
      movableColumns:true,
      persistence:{
        sort:true,
        filter:true,
        columns:true,
      },
      persistenceID:"dataSave"
    };
    return (
        <div className="form-page">
            <div className = 'form-group'>
                <div className = 'form-group'>
                    <label>Fecha de Inico</label>
                    <input onChange={e => this.change(e)} className= 'form-control' type = 'date' name='inicio' value= {this.state.ciudad}  />
                </div>
                <div className = 'form-group'>
                    <label>Fecha de vencimiento</label>
                    <input onChange={e => this.change(e)} className= 'form-control' type = 'date' name='final' value= {this.state.ciudad}  />
                </div>
                <div>
                <button type = 'button' onClick ={this.consultaVentas} className = 'btn btn-primary'> Guardar </button>
                </div>
            </div>
            <div>
                <React15Tabulator columns={columns} data={filteredData} options={options}/>
            </div>
        </div>
    );
  }
}

export default ListaPagos;
